import * as React from 'react';
import {
  DrawerContentScrollView,
  DrawerItem,
  DrawerItemList
} from '@react-navigation/drawer';
import  Ionicons  from 'react-native-vector-icons/Ionicons';  
import { View , Image, StyleSheet, Text, ImageBackground} from 'react-native';

import { AuthContext } from '../../App';

function DrawerContent(props) {
    const { signOut } = React.useContext(AuthContext);
    const [userID, setUserId ]= React.useState(0);
    const [img, setImg] = React.useState([]);
    const [isLoading, setLoading] = React.useState(true);

    const getData = async () => {
        try {
          const jsonValue = await AsyncStorage.getItem('userToken')
          setUserId(jsonValue != null ? parseInt(jsonValue) : null)
        } catch(e) {
          console.log(e)
        }
    }      

    React.useEffect(() => {
        getData();
      });

    console.log(userID)
    return (
      <DrawerContentScrollView {...props}>
        <ImageBackground
            source={require("../Images/header.png")}
            style={styles.imagebackground}
        >
            <Image 
                style={styles.profileIcon}
                source={{ uri: 'https://akinavn.vn/wp-content/uploads/2020/07/hinh-anh-buon-khoc-.jpg' }}
            />
            <View>
                <Text style={styles.title}>{props.userName}</Text>
                <Text style={styles.caption}>@j_doe</Text>
            </View>
        </ImageBackground>
        <View style={styles.itemList}>         
        <DrawerItemList {...props} />

            <DrawerItem 
            icon={({color, size}) => (
                <Ionicons 
                    name="ios-settings" 
                    color={color}
                    size={size}
                />
            )}
            label="Settings"
            drawerContentOptions={{
                activeBackgroundColor: '#93278f',
            }}
            onPress={() => props.navigation.navigate('Home')}
        />
        <DrawerItem 
            icon={({color, size}) => (
                <Ionicons 
                    name="ios-exit" 
                    color={color}
                    size={size}
                />
            )}
            label="Sign Out"
            drawerContentOptions={{
                activeBackgroundColor: '#93278f',
            }}
            onPress={() => {signOut()}}
        />
    </View>
      </DrawerContentScrollView>
    );
  } export {DrawerContent}

  const styles = StyleSheet.create({
    profileIcon: {
        width: 100, 
        height: 100, 
        borderRadius: 50, 
        marginTop: 20,
    },
    title: {
        fontSize: 16,
        marginTop: 3,
        fontWeight: 'bold',
        color: 'white'
    },
    caption: {
        fontSize: 14,
        lineHeight: 14,
        color: 'white'
    },
    imagebackground: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        width: "100%",
        height: "130%"
    },

    itemList: {
        marginTop: 50,
    }
  });
