import React, { Component } from 'react';
import {
    View, 
    StyleSheet, 
    Text, 
    ScrollView,
    TextInput,
    TouchableOpacity,
    Alert
} from 'react-native';
import { Dropdown } from 'react-native-material-dropdown-v2';

function NewStory (props){
    const [title, setTitle] = React.useState('');
    const [description, setDescription] = React.useState('');
    const [story_img, setStory_img] = React.useState(null);
    const [author_id, setAuthor_id] = React.useState(1);
    const [dataSource, setDataSource ]= React.useState([]);
    const [isLoading, setLoading] = React.useState(true);

    React.useEffect(() => {
        props.navigation.dangerouslyGetParent().setOptions({
            tabBarVisible: false
        });
        fetch('http://192.168.1.2:19000/category' )
        .then ((response) => response.json())
        .then ( (res) => { 
          console.log('kq:' + res); 
          setDataSource(res);
        })
        .catch ((error) => {
          console.log(error);
        })
        .finally(() => {
          setLoading(false) 
        });
      });

    const upload_story = () => {
      if (title.length == 0 || description.length == 0) {
        alert('Title and description is not empty');
      }else{
        fetch('http://192.168.1.2:19000/create_story' , {
          method: 'POST',
          headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            'title_story': title,
            'story_description': description,
            'story_img': story_img,
            'user_id': author_id
          })
      })
      .then ((response) => response.json())
      .then ( (res) => { 
        Alert.alert(
          "Your story created",
          "Do you want to access your story list to create new chapter",
          [
            {
              text: "Cancel",
              onPress: () => console.log("Cancel Pressed"),
              style: "cancel"
            },
            { text: "OK", onPress: () => props.navigation.navigate('YourStory') }
          ],
          { cancelable: false }
        ); 
      })
      .catch ((error) => {
        console.log(error);
      })
      }
    }
    return (               
        <View style={styles.main}>
            <Text style={{  fontSize: 16, fontWeight: 'bold'}}>Story title:</Text>
            <TextInput
            style={{ height: 40, backgroundColor: 'white' }}
                onChangeText={text => setTitle(text)}
            />
            <Dropdown
                    label='Category of story'
                    data={dataSource}
                  />
            <Text style={{ marginTop: 10, fontSize: 16, fontWeight: 'bold'}}>Story description:</Text>  
            <ScrollView scrollEnabled  style={{ marginTop: 10, backgroundColor: 'white'}} >
                <TextInput multiline placeholder="Description for your story" scrollEnabled={false} onChangeText={text => setDescription(text)} />
            </ScrollView>
            <TouchableOpacity style={styles.upload} onPress={()=> {upload_story();}}><Text style={{textAlign: 'center', color: 'white'}}>Upload your story</Text></TouchableOpacity>
        </View>
    );
}
export {NewStory};


var styles = StyleSheet.create({
    main: {
        flex: 1,
        paddingLeft: 30,
        paddingRight: 30,
        paddingBottom: 1,
        alignItems: 'stretch',
    },
    upload:{
        marginTop: 20,
        paddingTop: 15,
        paddingBottom: 15,
        backgroundColor:'#aa4fff',
        borderRadius: 50,
        borderWidth: 1,
        borderColor: '#fff'
    },
});
