import React from 'react';
import { StyleSheet, View, Text, Image } from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';
import {
    DrawerContentScrollView,
    DrawerItem,
    DrawerItemList
} from '@react-navigation/drawer';
import  Ionicons  from 'react-native-vector-icons/Ionicons'; 
import { AuthContext } from '../../../App';

function DrawerContent(props) {
  const [dataSource, setDataSource ]= React.useState([]);
  const [isLoading, setLoading] = React.useState(true);
  const getData = async () => {
    try {
      AsyncStorage.getItem('user').then((value) => {
        const jsonValue = JSON.parse(value);
        setDataSource(jsonValue);
      })
    } catch(e) {
      console.log(e)
    }
  }
  console.log('sssssss' + dataSource)
  React.useEffect(() => {
    getData();
  }, []);
  return (
    <View>
        <DrawerContentScrollView {...props}>
          <View>
              <Image 
                  style={styles.profileIcon}
                  source={{ uri: 'https://akinavn.vn/wp-content/uploads/2020/07/hinh-anh-buon-khoc-.jpg' }}
              />
              <View>
                <Text style={styles.title}>{dataSource.name}</Text>
                <Text style={{fontSize: 16,
        marginTop: 3,
        fontWeight: 'bold',
        color: 'black'}}>@{dataSource.username}</Text>
              </View>
          </View>
          <View style={styles.itemList}>         
            <DrawerItemList {...props} />
              <DrawerItem 
                icon={({color, size}) => (
                    <Ionicons 
                        name="ios-settings" 
                        color={color}
                        size={size}
                    />
                )}
                label="Settings"
                drawerContentOptions={{
                    activeBackgroundColor: '#93278f',
                }}
                onPress={() => props.navigation.navigate('Home')}
              />
            <DrawerItem 
                icon={({color, size}) => (
                    <Ionicons 
                        name="ios-exit" 
                        color={color}
                        size={size}
                    />
                )}
                label="Sign Out"
                drawerContentOptions={{
                    activeBackgroundColor: '#93278f',
                }}
                onPress={() => {signOut()}}
            />
          </View>
        </DrawerContentScrollView>  
    </View>
  )   
} export {DrawerContent};

const styles = StyleSheet.create({
    profileIcon: {
        width: 80, 
        height: 80, 
        borderRadius: 50, 
        marginTop: 20,
        marginLeft: 10
    },
    title: {
        fontSize: 16,
        marginTop: 3,
        fontWeight: 'bold',
        color: 'black'
    },
    caption: {
        fontSize: 14,
        lineHeight: 14,
        color: 'black'
    },
    imagebackground: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        width: "100%",
        height: "130%"
    },

    itemList: {
        marginTop: 50,
    }
  });
