import React from 'react';
import { StyleSheet, Dimensions, View, TextInput, Text, FlatList } from 'react-native';
import io from "socket.io-client";
import AsyncStorage from '@react-native-community/async-storage';
import { Value } from 'react-native-reanimated';
import { DrawerItemList } from '@react-navigation/drawer';


function RingScreen() {
  const [dataSource, setDataSource ]= React.useState([]);
  const getData = async () => {
    try {
      AsyncStorage.getItem('user').then((value) => {
        const jsonValue = JSON.parse(value);
        console.log('sssssss', jsonValue);
        fetch('http://192.168.1.2:19000/book_author/' + jsonValue.user_id)
        .then ((response) => response.json())
        .then ( (res) => { 
          setDataSource(res);
        })
        .catch ((error) => {
          console.log(error);
        })
        .finally(() => {
          setLoading(false) 
        });
      })

    } catch(e) {
      console.log(e)
    }
  }
  console.log('sssssss' + dataSource)
  React.useEffect(() => {
    getData();
  }, []);
  return (
      <View style={styles.container}>
        <FlatList
            data={dataSource}
            keyExtractor= {item => item.story_id.toString()}
            renderItem={({ item }) => (
              <TouchableOpacity onPress={() =>props.navigation.navigate('DetailStory', {item: item})}>         

            <Text>{item.user_id}</Text>
              </TouchableOpacity>
            )}
            showsHorizontalScrollIndicator={false}
            ListEmptyComponent={ListEmpty}
          /> 
    </View>
  )   
} export {RingScreen};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'center',
        marginTop: 25,
    },

});
