import React from 'react';
import { StyleSheet, Image, View, Dimensions, Text, AsyncStorage} from 'react-native';
import { AntDesign } from '@expo/vector-icons'; 
import { TouchableOpacity } from 'react-native-gesture-handler';

function  HeaderAboutPages(props) {
  const [dataSource, setDataSource ]= React.useState([]);
  
  const getData = async () => {
    try {
      AsyncStorage.getItem('user').then((value) => {
        const jsonValue = JSON.parse(value);
        setDataSource(jsonValue);
      })
    } catch(e) {
      console.log(e)
    }
  }

  React.useEffect(() => {
    getData();
  },[]);
 
  return (
    <View>
        <View style={style.container}>
          <View style={{flexDirection: 'row'}}>
            <View style={{flex: 1, alignItems: 'center'}}>
              <Image style={style.image}
                  source={{uri: dataSource.user_avatar}} 
              />
            </View>
            <TouchableOpacity  style={{justifyContent: 'flex-end'}} onPress={()=> props.navigation.navigate('settings')}>
              <AntDesign name="setting" size={24} color="black"/>
            </TouchableOpacity>
          </View>
          <Text style={{marginTop: 5, color: '#aa4fff', fontSize: 18}}>@{dataSource.username}</Text>
          <View style={{flex:1}}> 
            <View style={{flex:1, flexDirection: 'row'}}>
              <Text style={{color: '#aa4fff'}}>{dataSource.num_following} following</Text>
              <Text style={{color: '#aa4fff', marginLeft: 10}}>{dataSource.num_follower} follower</Text>
            </View>
          </View>  
        </View>
    </View>
  );
} export {HeaderAboutPages}

const style = StyleSheet.create({
    container: {
        padding: 5,
        width: Dimensions.get('window').width, 
        height: 210,
        justifyContent: 'center',
        alignItems: 'center'
    },
    image: {
        marginTop: 20,
        width: 100, 
        height: 100, 
        borderRadius: 50
    },
});
