import React from 'react';
import { View, FlatList,TouchableOpacity,TextInput } from 'react-native';
import {ListStory, FamousReader} from '../../Components/HomeScreen';
import {Category} from '../../Components/CategoryComponent';
import  Ionicons  from 'react-native-vector-icons/Ionicons';


function HomeScreen(props) {
  const data = [
    {id:"1"}
  ]
  const [value, setValue] = React.useState('');
  return (
    <View style={{flex: 1, backgroundColor: 'white'}}> 
      <View style={{flex: 10}}>
        <View style={{height:80, padding: 10, alignItems:'center', backgroundColor:'#8a2be2', flexDirection:'row'}}>    
            <TouchableOpacity style={{flex:1}} onPress={()=> props.navigation.toggleDrawer()}>
                <Ionicons name='md-menu' size={50} color={'white'}/>
            </TouchableOpacity>
            <View style={{backgroundColor: 'white', alignItems:'center',flexDirection:'row', flex: 5, borderRadius:40}}>
                <Ionicons name='md-search' size={30} style={{marginLeft:10}}/>
                <TextInput 
                  placeholder='Search'
                  onChangeText={text => setValue(text)}
                  returnKeyType= 'search'
                  value={value}
                  onSubmitEditing={()=> props.navigation.navigate('result', {search: value})}
                />
            </View>
            <TouchableOpacity style={{flex:1}} onPress={()=> props.navigation.navigate('DetailUser')}>
                <Ionicons name='ios-person' size={40} color={'white'} style={{marginLeft:10}}/>
            </TouchableOpacity>
        </View>
        <FlatList
          data={data}
          renderItem={(item) => (
            <>
              <Category {...props}/> 
              <FamousReader {...props}/>
              <ListStory {...props} />
            </>
          )}
          showsVerticalScrollIndicator = {false}
          keyExtractor={item => item.id}
          />
      </View>
    </View>
  )

}
export {HomeScreen};
