import React from 'react';
import { StyleSheet, View, Text, TextInput, TouchableOpacity, ScrollView } from 'react-native';

 
function NewPart() {
    const [title, setTitle] = React.useState('');
    const [chapterContent, setChapterContent] = React.useState('');
    const storyId = 1;

    const upload_chapter = () => {
        if(title.length > 0 || chapterContent.length > 0){
            fetch('http://192.168.1.2:19000/upload_chapter' , {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    'story_id': storyId,
                    'chapter_name': title,
                    'chapter_content': chapterContent
                })
            })
            .then ((response) => response.json())
            .then ( (res) => { 
                alert('Succesfull!!') 
            })
            .catch ((error) => {
                console.log(error);
            })
        } else {
            alert('Title and content must have content')
        }
    }

    return (
        <View style={styles.main}>
            <Text style={{  fontSize: 16, fontWeight: 'bold'}}>Chapter title:</Text>
            <TextInput
            style={{ height: 40, backgroundColor: 'white' }}
                onChangeText={text => setTitle(text)}
            />
            <Text style={{ marginTop: 10, fontSize: 16, fontWeight: 'bold'}}>Content of chapter:</Text>  
            <ScrollView scrollEnabled  style={{ marginTop: 10, backgroundColor: 'white'}} >
                <TextInput multiline placeholder="Chapter's content of your story" scrollEnabled={false} onChangeText={text => setChapterContent(text)} />
            </ScrollView>
            <TouchableOpacity style={styles.upload} onPress={()=> {upload_chapter();}}><Text style={{textAlign: 'center', color: 'white'}}>Upload your story</Text></TouchableOpacity>
        </View>
    )
} export {NewPart};
 
var styles = StyleSheet.create({
    main: {
        flex: 1,
        paddingLeft: 30,
        paddingRight: 30,
        paddingBottom: 1,
        alignItems: 'stretch',
    },
    upload:{
        marginTop: 20,
        paddingTop: 15,
        paddingBottom: 15,
        backgroundColor:'#aa4fff',
        borderRadius: 50,
        borderWidth: 1,
        borderColor: '#fff'
    },
});
