import * as React from 'react';
import {View, Text, Image, StyleSheet, TouchableOpacity, FlatList } from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons'
// import { TouchableWithoutFeedback } from 'react-native-gesture-handler';
import {ButtonReadingAdd, ListChapterModal} from '../../Components/DetailStoryScreen'

function DetailStoryScreen(props) {
  const [isLoading, setLoading] = React.useState(false)
  const [userData, setUserData] = React.useState([]);
  const data = [
    {id:"1"}
  ]

  React.useEffect(() => {
    fetch('http://192.168.1.6:19000/user/' + props.route.params.item.author_id)
    .then ((response) => response.json())
    .then ( (res) => { 
      setUserData(res);
      console.log(res);
    })
    .catch ((error) => {
      console.log(error);
    })
    .finally(() => {
      setLoading(false) 
    });
  });

  return (
    <View style={{ flex: 1, backgroundColor: 'white'}}>

          <View style={style.container}>
            <TouchableOpacity>
              <Image style={style.image}
                  source={{uri: props.route.params.item.story_img}} 
              />
            </TouchableOpacity>
              <Text style={{fontSize: 30, marginTop: 10}}>{props.route.params.item.story_title}</Text>
            <View>
              <FlatList
                data={userData}
                keyExtractor= {item => item.user_id.toString()}
                renderItem={({ item }) =>(
                  <TouchableOpacity style={style.author} onPress={() => props.navigation.navigate('DetailUser', {item: item})}>
                    <Image style={style.avatar}
                        source={{uri: item.user_avatar}} 
                    />
                    <Text style={{marginLeft: 5}}>@{item.username}</Text>
                  </TouchableOpacity>
                )}
              />
              <View style={{flexDirection: 'row', alignItems: 'center'}}>
                <Icon name='ios-star' size={14}/>
                <Text style={{marginLeft: 5}}> 
                  {props.route.params.item.vote} Votes</Text>
                <Icon name='ios-list' size={14} style={{marginLeft: 10}}/>
                <Text style={{marginLeft: 5}}>
                  {props.route.params.item.parts} Parts</Text>
              </View>
            </View>
          </View>
          <ButtonReadingAdd {...props}/>
          <View style={style.description}>
              <Text style={{fontSize: 16}}>{props.route.params.item.story_description}</Text>
          </View>
          <ListChapterModal {...props}/>


    </View>
  );
}
export {DetailStoryScreen};

const style = StyleSheet.create({
  container: {
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 30,
  },
  image: {
    width: 150,
    height: 200
  },
  author: {
    flexDirection: 'row', 
    justifyContent: 'center',
    alignItems: 'center',
  },
  avatar: {
    height: 30,
    width: 30,
    borderRadius: 50
  },

  description: {
    padding: 20,
  },


})
