      width: Dimensions.get('window').width,  
      backgroundColor: 'white', 
      alignItems: 'center', 
      justifyContent: 'space-around', 
      flexDirection: 'row'
    },
    modalView: {
      backgroundColor: "white",
      shadowColor: "#000",
      width: Dimensions.get('window').width, 
      height: Dimensions.get('window').height,
    },
    HeaderModal: {
      justifyContent: 'flex-start',
      alignItems: 'center',
      flexDirection: 'row',
      backgroundColor: '#EEEEEE',
      height: 40,
      shadowColor: "#000",
