import React from 'react';
import { StyleSheet, Dimensions, View, TextInput, Text, FlatList } from 'react-native';
import io from "socket.io-client";
import AsyncStorage from '@react-native-community/async-storage';
import { Value } from 'react-native-reanimated';


function RingScreen() {
  const [dataSource, setDataSource ]= React.useState([]);
  const getData = async () => {
    try {
      AsyncStorage.getItem('user').then((value) => {
        const jsonValue = JSON.parse(value);
        console.log('sssssss', jsonValue);
        setDataSource(jsonValue);
      })

    } catch(e) {
      console.log(e)
    }
  }

  React.useEffect(() => {
    getData();
  }, []);
  return (
      <View style={styles.container}>
        <FlatList
            data={dataSource}
            keyExtractor= {item => item.user_id.toString()}
            renderItem={({ item }) => (
              <TouchableOpacity onPress={() => props.navigation.navigate('DetailStory', {item: item})}>         
                <Text>{item.username}xxxsxsasasasasasa</Text>
              </TouchableOpacity>
            )}
            showsHorizontalScrollIndicator={false}
          />
    </View>
  )   
} export {RingScreen};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'center',
        marginTop: 25,
    },

});
