import React, { useState } from "react";
import {
  Alert,
  Modal,
  StyleSheet,
  Text,
  Dimensions,
  TouchableHighlight,
  TouchableOpacity,
  TouchableWithoutFeedback,
  View
} from "react-native";
import Icon from 'react-native-vector-icons/Ionicons'
import { FlatList, TextInput } from "react-native-gesture-handler";

const ButtonReadingAdd = (props) => {
  const [modalVisible, setModalVisible] = useState(false);
  const [isLoading, setLoading] = useState(false);
  const [modalVisible2, setModalVisible2] = useState(false);
  const [dataSource, setDataSource] = useState([]);
  const [data, setData] = useState([]);
  const [value, setValue] = React.useState('');
  
  function get_list() {
    fetch('http://192.168.1.6:19000/get_list/1')
    .then ((response) => response.json())
    .then ( (res) => { 
      console.log('list:' + res); 
      setData(res);
    })
    .catch ((error) => {
      console.log(error);
    })
    .finally(() => {
      setLoading(false) 
    });
  }

  function create_list(){
    fetch('http://192.168.1.6:19000/create_list' , {
          method: 'POST',
          headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            'user_id': 1,
            'list_name': value
          })
      })
      .then ((response) => response.json())
      .then ( (res) => { 
        setModalVisible2(false);
      })
      .catch ((error) => {
        console.log(error);
      })
  }

  function add_to_list(id){
    fetch('http://192.168.1.6:19000/add_to_list/' + id + '/' + props.route.params.item.story_id)
    .then ((response) => response.json())
    .then ( (res) => { 
      setDataSource(res);
    })
    .catch ((error) => {
      console.log(error);
    })
    .finally(() => {
      setLoading(false) 
    });
  }
  
  return (
    <View {...props}>
      <View style={styles.button}>
            <TouchableOpacity onPress={() => props.navigation.navigate('Reading', {chapter_id: 1, item: props.route.params.item})}>
              <View style={styles.reading}>
                <Text style={styles.readingText}>Reading</Text>
              </View>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setModalVisible(true);
                get_list();
              }}
            >
              <View>
                <Icon name='ios-add-circle-outline' style={styles.addButton}/>
              </View>                 
            </TouchableOpacity>
      </View>
      <TouchableWithoutFeedback
        onPressOut={() => {
          setModalVisible(false);
        }}
      >
        <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <FlatList 
              data={data}
              keyExtractor={item => item.list_id}
              renderItem={({item}) => (
                <TouchableOpacity 
                onPress={()=> {
                  add_to_list(item.list_id);
                  setModalVisible(!modalVisible);
                }}>
                  <Text style={styles.modalText}>{item.list_name}</Text>
                </TouchableOpacity>
              )}
            />
            
            <TouchableHighlight
              onPress={() => {
                setModalVisible(!modalVisible);
                setModalVisible2(!modalVisible2);
              }}
            >
              <Text style={styles.modalText}>Create new list</Text>
            </TouchableHighlight>
            <TouchableHighlight
              onPress={() => {
                setModalVisible(!modalVisible);
              }}
            >
              <Text style={styles.modalText}>Add to libary</Text>
            </TouchableHighlight>
          </View>
        </View>
      </Modal>
      </TouchableWithoutFeedback>
      <Modal
          animationType="slide"
          transparent={true}
          visible={modalVisible2}
          onRequestClose={() => {
            setModalVisible2(!modalVisible2);
          }}
        >
          <View style={styles.centeredView}>
            <View style={styles.modalView}>
              <Text style={{fontSize: 18, fontWeight: 'bold'}}>Create New List</Text>
              <TextInput placeholder='Enter name of list'
                onChangeText={text => setValue(text)}
                onSubmitEditing={()=> create_list()}
                returnKeyType='done'
              />
            </View>
          </View>
        </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    marginTop: 22,
    flexDirection: 'column',
    justifyContent: "flex-end"
  },
  modalView: {
    backgroundColor: "white",
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    //maxheight: Dimensions.get('window').height * 0.3
  },
  openButton: {
    backgroundColor: "#F194FF",
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  },
  button: {
    marginTop: 10, 
    flexDirection: 'row', 
    justifyContent: 'center', 
    alignItems: 'center'
  },
  reading: {
    height: 40, width: 200, 
    backgroundColor: '#aa4fff', 
    borderRadius: 40, 
    justifyContent: 'center', 
    alignItems: 'center'
  },
  readingText: {
    marginLeft: 5, 
    fontWeight: 'bold', 
    fontSize: 20, 
    color: 'white' 
  },
  addButton: {
    color: '#aa4fff', 
    fontSize: 40, 
    marginLeft: 5
  }
});

export {ButtonReadingAdd};
