import React from 'react';
import { createStackNavigator } from "@react-navigation/stack";
import { Ionicons } from '@expo/vector-icons';
import { TouchableOpacity } from 'react-native-gesture-handler';

import {HomeScreen} from '../ScreemFolder/OtherScreen';
import {ProfileScreen} from '../ScreemFolder/UserScreen';
import {
  UserStoryScreen, 
  NewStory, 
  CategoryScreen, 
  ReadingScreen, 
  DetailStoryScreen,
  NewPart
} from '../ScreemFolder/StoryScreen'
import { ModalChapterContent } from '../Components/ReadingScreen';

const HomeStack = createStackNavigator();
const HomeStackScreen = (props, {navigation}) => (
<HomeStack.Navigator mode="modal">
    <HomeStack.Screen 
      name="Home" 
      component={HomeScreen}
      options = {{
       headerShown: false
      }}
    />  
    <HomeStack.Screen 
      name="DetailStory" 
      options = {{
        title: 'Detail story',
        headerStyle: {
          backgroundColor: '#8a2be2',
        },
        headerTintColor: '#fff',
       }}
      component={DetailStoryScreen}
    />
    <HomeStack.Screen 
      name="Reading" 
      label= 'Reading' 
      component={ReadingScreen} 
      options={{
        tabBarVisible: false,
        headerStyle: {
          backgroundColor: '#8a2be2',
        },
        headerTintColor: '#fff',
        headerRight: ()=> (
          <TouchableOpacity>
            <Ionicons name="md-menu" size={40} color="white" style={{marginRight: 20}}/>
          </TouchableOpacity>
        )
      }}
    />
    <HomeStack.Screen 
      name="DetailCategory" 
      options = {{
        title: 'Category story',
        headerStyle: {
          backgroundColor: '#8a2be2',
        },
        headerTintColor: '#fff',
       }} 
      component={CategoryScreen}/>
    <HomeStack.Screen 
      name="DetailUser" 
      options = {{
        title: '',
        headerStyle: {
          backgroundColor: '#8a2be2',
        },
        headerTintColor: '#fff',
       }}  
      component={ProfileScreen} 
    />
    <HomeStack.Screen
      name="YourStory"
      component={UserStoryScreen} 
      options = {{
        title: 'Your story',
        headerStyle: {
          backgroundColor: '#8a2be2',
        },
        headerTintColor: '#fff',
        headerRight: () => (
          <TouchableOpacity onPress={()=> navigation.navigate('NewStory')}>
            <AntDesign name="addfile" size={24} color="white" style={{marginRight: 10}} />
          </TouchableOpacity>
        ),
      }}

    />
    <HomeStack.Screen
      name="NewStory"
      component={NewStory} 
      options = {{
        title: 'Write your story',
          headerStyle: {
            backgroundColor: '#8a2be2',
          },
          headerTintColor: '#fff',
        }}
    />
    <HomeStack.Screen
      name="NewPart"
      component={NewPart} 
      options = {{
        title: 'Write your chapter',
          headerStyle: {
            backgroundColor: '#8a2be2',
          },
          headerTintColor: '#fff',
        }}
    />
  </HomeStack.Navigator>
)
export {HomeStackScreen};
