import * as React from 'react';
import { StyleSheet, View, Text, TouchableOpacity, Modal, Image, FlatList, TouchableWithoutFeedback} from 'react-native';
import { AntDesign, Ionicons } from '@expo/vector-icons'; 

function ReadingHeader(props) {
    const [isLoading, setLoading] = React.useState(false)
    const [modalVisible, setModalVisible] = React.useState(false);
    const [dataSource, setDataSource] = React.useState([]);
    React.useEffect(() => {
        // fetch('http://10.0.2.2:5000/')
        fetch('http://192.168.1.6:19000/chapter/' + props.route.params.item.story_id)
        .then ((response) => response.json())
        .then ( (res) => { 
          setDataSource(res);
        })
        .catch ((error) => {
          console.log(error);
        })
        .finally(() => {
          setLoading(false) 
        });
      });

      function ListEmpty() {
        return (
          //View to show when list is empty
          <View style={style.MainContainer}>  
              <View>
                <Text style={{fontSize: 20, color: 'black', marginTop: 20, marginBottom: 20}}>Sorry! There are no chapter.</Text>
              </View>
          </View>
        );
      };

    return (
      <View style={{ flex: -2, backgroundColor: '#8a2be2', height: 80, width: '100%'}} {...props}>
        <View style={{marginTop: 20, flexDirection: 'row'}}>
            <View style={{flex: 1}}>
                <TouchableOpacity onPress={() => {
                    props.navigation.goBack(); 
                    props.navigation.dangerouslyGetParent().setOptions({
                     tabBarVisible: false
                    });
                }}>
                    <AntDesign name="left" size={24} color="white" />
                </TouchableOpacity>
            </View>
            <View style={{flex: 5}}><Text style={{color: 'white', fontSize: 18, fontWeight: 'bold'}} numberOfLines={1}>{props.route.params.item.story_title}</Text></View>
            <View style={{flex: 1}} >
                <TouchableOpacity style={{flex:1}} onPress={() => {
                    setModalVisible(true);
                }}>
                    <Ionicons name='md-menu' size={40} color={'white'}/>
                </TouchableOpacity>
            </View>
        </View>

        <TouchableWithoutFeedback onPress={() => setModalVisible(!modalVisible)}>
        <Modal
            animationType="slide"
            transparent={true}
            visible={modalVisible}
            onRequestClose={() => {setModalVisible(!modalVisible);}}
        >
            <View style={style.centeredView}>
            <View style={style.modalView}>        
                <View style={{alignItems: "center"}}>
                    <Image style={style.imageModal}
                        source={{uri: props.route.params.item.story_img}} 
                    />
                    <Text style={style.titleModal} numberOfLines={2}>{props.route.params.item.story_title}</Text>
                </View>
                <FlatList
                    data={dataSource}
                    keyExtractor= {item => item.chapter_id.toString()}
                    renderItem={({ item }) => (
                    <TouchableOpacity 
                        onPress={() => props.navigation.navigate('Reading', {chapter_id: item.chapter_id, item: item}, 
                        setModalVisible(!modalVisible)
                        )} style={style.chapter}>         
                        <Text numberOfLines={1}>{item.chapter_name}</Text>
                    </TouchableOpacity>
                    )}
                    showsHorizontalScrollIndicator={false}
                    ListEmptyComponent={ListEmpty}
                />
                    
                <TouchableOpacity
                style={style.closeButtom}
                onPress={() => {
                    setModalVisible(!modalVisible);
                }}
                >
                <Text style={style.textStyle}>Hide chapter list</Text>
                </TouchableOpacity>
            </View>
            </View>
        </Modal> 
        </TouchableWithoutFeedback>
      </View>
    );
}
export {ReadingHeader};

const style = StyleSheet.create({
    parts: {
        padding: 10,
        borderBottomWidth: 0.5,
        borderTopWidth: 0.5,
      },
    openChapterList: {
        fontSize: 18,
        color: '#aa4fff',
        fontWeight: 'bold'
      },
    
    centeredView: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        marginTop: 22
    },
    modalView: {
        margin: 10,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 10,
        width: '80%',
        height: '90%',
        //alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
        width: 0,
        height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5
    },
    textStyle: {
        color: "white",
        fontWeight: "bold",
        textAlign: "center"
    },
    modalText: {
        marginBottom: 15,
        textAlign: "center"
    },
    imageModal: {
        width: 150,
        height: 200,
        marginTop: 20,
    },
    titleModal: {
        fontSize: 15, 
        fontWeight: 'bold', 
        marginTop: 10, 
        marginBottom: 10
    },
    chapter: {
        padding: 10,
        borderBottomWidth: 0.5,
        borderBottomColor: 'grey'
    },
    closeButtom: {
        backgroundColor: "#aa4fff",
        borderRadius: 20,
        padding: 10,
        elevation: 2,
    }
});
