import React from 'react';
import { StyleSheet, Dimensions, View, TextInput, Text } from 'react-native';
import io from "socket.io-client";
import { Ionicons } from '@expo/vector-icons'; 
import { TouchableOpacity } from 'react-native-gesture-handler';

 
function ChatScreen() {
    const [chatMessage, setChatMessage] = React.useState([]);
    const [contentMassage, setContentMassage] = React.useState('');
    const [message, setMessage] = React.useState([]);
    const socket = io("http://192.168.1.6:19000");
    React.useEffect(() => {
      socket.on("get_message",  function(data){
        setChatMessage(data);
      })
    });
    // React.useEffect(() => {
    //   //connect with socket.io
    //     this.socket = io("http://192.168.1.6:19000:19000");
    //   //get list story of meassage
    //     this.socket.on("get_message",  msg => {
    //     setChatMessages( [...chatMessages, msg]);
    //   });
    // });
    // // when submit will call two function 
    //   //emit_message to display in screen  
    //   //add_message to add into db 
    //   //then set massage = ""
    // submitChatMessage =()=> {
    //     this.
    //     this.
    //     
    // }

    // const messages = chatMessages.map(messages => (
    //     <Text key={messages}>{chatMessage}</Text>
    //   ));

    function submitChatMessage() {
      setMessage([
        {
          'contact_id': 1,
          'content': contentMassage,
          'upload_by': 1
        }
      ])
      socket.emit("emit_message", message);
      socket.emit("add_message", message);
      setContentMassage("");
      setMessage([]);
    }

    const renderMassages = chatMessage.map(renderMassages => (
      <Text key={renderMassages}>{messages}</Text>
    ));

    console.log('dssdsdsdsdsd' + chatMessage);
  
    return (
        <View style={styles.container}>
        <View style={{flex: 1}}>
          <Text>{renderMassages}</Text>
        </View>
        <View style={{justifyContent: 'flex-end', backgroundColor: 'white', width: '100%', height: 45, flexDirection: 'row', paddingRight: 5}}>
          <TextInput
            style={{ height: 45, borderColor: '#8a2be2', borderWidth: 1, width: '80%'}}
            autoCorrect={false}
            value={contentMassage}
            onSubmitEditing={() => submitChatMessage()}
            onChangeText={text =>  setContentMassage(text)}
          />
          <TouchableOpacity style={{backgroundColor: '#aa4fff'}}>
            <Ionicons name="md-send" size={45} color="white" style={{ marginLeft: 10, marginRight: 10}} />
          </TouchableOpacity>
        </View>
      </View>
    )   
} export {ChatScreen};
 
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'flex-start',
        flexDirection: 'column',
        alignItems: 'center',
        marginTop: 25,
    },

});
