import React, { useState } from 'react';
import { FlatList, StyleSheet, Image, View, TouchableOpacity, Text} from 'react-native';
import {SplashScreen} from '../../ScreemFolder/OtherScreen';

function  FamousReader(props) {
  const [isLoading, setLoading] = React.useState(false);
  const [dataSource, setDataSource ]= React.useState([]);

  React.useEffect(() => {
    // fetch('http://10.0.2.2:5000/')
    fetch('http://192.168.1.6:19000/famous')
    .then ((response) => response.json())
    .then ( (res) => { 
      console.log('kq:' + res); 
      setDataSource(res);
    })
    .catch ((error) => {
      console.log(error);
    })
    .finally(() => {
      setLoading(false) 
    });
  });

  return (
    <View>
      {isLoading? <SplashScreen/>: (
        <View>
          <Text style={style.text}>Famous Reader</Text>
          <View style={style.container}>
            <FlatList
              data={dataSource}
              horizontal
              showsHorizontalScrollIndicator={false}
              keyExtractor={item => item.user_id.toString()}
              renderItem={({item}) => (
                <TouchableOpacity onPress={() => props.navigation.navigate('DetailUser', {item: item})}>
                    <Image style={style.image}
                        source={{uri: item.user_avatar}} 
                    />
                </TouchableOpacity>

              )}
            />
        </View>
        </View>
      )}
    </View>
  );
} export {FamousReader}

const style = StyleSheet.create({
    container: {
        padding: 5,

    },
    image: {
        marginRight: 10,
        width: 70, 
        height: 70, 
        borderRadius: 50
    },
    text: {
      margin: 5,
      fontSize: 20,
      fontWeight: 'bold',
    }
});
