import React from 'react'
import {Text, View, Dimensions, TouchableOpacity, StyleSheet, Modal} from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons'; 
import { Ionicons } from '@expo/vector-icons'; 

function ReadingTabComponent(props) {
  const [vote, setVote] = React.useState(props.route.params.item.vote);
  const [voting, setVoting] = React.useState(false);
  const [modalVisible, setModalVisible] = React.useState(false);

  React.useEffect(() => {
      props.navigation.dangerouslyGetParent().setOptions({
          tabBarVisible: false
      });
  });

  function sendVote() {
    fetch('http://192.168.1.8:19000/vote/' + props.route.params.item.story_id + '/' + vote)
    .then ((response) => response.json())
    .then ( (res) => { 
        console.log('kq:' + res); 
    })
    .catch ((error) => {
        console.log(error);
    })
  }

  
  return(
      <View style={{flex: 1, height: 40,  width: Dimensions.get('window').width,  backgroundColor: 'white', alignItems: 'center', justifyContent: 'space-around', flexDirection: 'row'}}>
          <TouchableOpacity onPress={() => {sendVote(); setVoting(!voting); setVote(voting?vote-1:vote+1)}}>
              <Ionicons 
                  name= {voting?'md-star':'md-star-outline'}
                  size={24} 
                  color={'#aa4fff'} 
              />
          </TouchableOpacity>
          <TouchableOpacity 
            onPress={() => {
              setModalVisible(true);
            }}>
              <MaterialCommunityIcons 
                  name="comment-outline" 
                  size={24} 
                  color={'#aa4fff'} 
              />
          </TouchableOpacity>
          <Modal
              animationType="slide"
              transparent={true}
              visible={modalVisible}
              onRequestClose={() => {setModalVisible(!modalVisible);}}
            >
              <View style={style.centeredView}>
                <View style={style.modalView}>
                  <TouchableOpacity
                    style={style.closeButtom}
                    onPress={() => {
                      setModalVisible(!modalVisible);
                    }}
                  >
                    <Text style={style.textStyle}>Hide chapter list</Text>
                  </TouchableOpacity>
                </View>
              </View>
            </Modal> 
          <TouchableOpacity>
              <MaterialCommunityIcons name="share-variant" size={24} color={'#aa4fff'} />
          </TouchableOpacity>
      </View>
  ); 
} export {ReadingTabComponent}

var style = StyleSheet.create({
    centeredView: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        marginTop: 22
      },
      modalView: {
        margin: 10,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 10,
        width: '80%',
        height: '90%',
        //alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5
      },
      textStyle: {
        color: "white",
        fontWeight: "bold",
        textAlign: "center"
      },
})
