import * as React from 'react';
import { StyleSheet, View, Text, TouchableOpacity, Modal, Image, ToastAndroid, AsyncStorage, TextInput, Dimensions, KeyboardAvoidingView, ScrollView} from 'react-native';
import { AntDesign, Feather} from '@expo/vector-icons'; 
import * as ImagePicker from 'expo-image-picker';

function SettingsScreen(props) {
  const [image, setImage] = React.useState(null);
  const [dataUser, setDataUser ]= React.useState([]);
  const [modalVisible, setModalVisible] = React.useState(false);
  const [modalVisible2, setModalVisible2] = React.useState(false);
  const [password, setPassword] = React.useState(null);
  const [fullname, setFullname] = React.useState(null);
  const [username, setUsername] = React.useState(null);
  const [email, setEmail] = React.useState(null);
  const [display, setDisplay] = React.useState(true);
  let formdata = new FormData();

  formdata.append("user_id", dataUser.user_id)
  formdata.append("email", email)
  formdata.append("fullname", fullname)
  formdata.append("username", username)
  formdata.append("files", {name: 'quyendog.jpg' ,uri: '', type:'image/jpeg'})

  const getData = async () => {
    try {
      AsyncStorage.getItem('user').then((value) => {
        const jsonValue = JSON.parse(value);
        setDataUser(jsonValue);
        setImage(jsonValue.user_avatar);
        setFullname(jsonValue.name);
        setEmail(jsonValue.email);
        setUsername(jsonValue.username);
        setPassword('123456');
      })
    } catch(e) {
      console.log(e)
    }
  }
  React.useEffect(() => {
    getData();
  }, []);
  const pickImage = async () => {
    (async () => {
      if (Platform.OS !== 'web') {
        const { status } = await ImagePicker.requestCameraRollPermissionsAsync();
        if (status !== 'granted') {
          alert('Sorry, we need camera roll permissions to make this work!');
        } else {
          let result = await ImagePicker.launchImageLibraryAsync({
