import React, { useState } from 'react';
import { FlatList, StyleSheet, Image, View, Dimensions, Text, TouchableOpacity} from 'react-native';
import {SplashScreen} from '../../ScreemFolder/OtherScreen';
import { Feather } from '@expo/vector-icons'; 

function  HeaderUserPages(props) {
  const [isLoading, setLoading] = React.useState(false);
  const [dataSource, setDataSource ]= React.useState([]);
  
  React.useEffect(() => {
    // fetch('http://10.0.2.2:5000/')
    fetch('http://192.168.1.2:19000/author/' + props.route.params.item.user_id)
    .then ((response) => response.json())
    .then ( (res) => { 
      console.log('kq:' + res); 
      setDataSource(res);
    })
    .catch ((error) => {
      console.log(error);
    })
    .finally(() => {
      setLoading(false) 
    });
  });

  return (
    <View>
      {isLoading? <SplashScreen/>: ( 
        <FlatList
          data={dataSource}
          horizontal
          showsHorizontalScrollIndicator={false}
          keyExtractor={item => item.user_id.toString()}
          renderItem={({item}) => (
            <View style={style.container}>
                <Image style={style.image}
                    source={{uri: item.user_avatar}} 
                />
                <Text style={{marginTop: 5, color: '#aa4fff', fontSize: 18}}>@{item.username}</Text>
                <View style={{flex:1}}> 
                  <View style={{flex:1, flexDirection: 'row'}}>
                    <Text style={{color: '#aa4fff'}}>{item.num_following} following</Text>
                    <Text style={{color: '#aa4fff', marginLeft: 10}}>{item.num_follower} follower</Text>
                  </View>
                  <TouchableOpacity style={style.following}>
                    <Feather name="check" size={24} color="#aa4fff" />
                    <Text style={{color: "#aa4fff", fontSize: 18, marginLeft: 5}}>Following</Text>
                  </TouchableOpacity>
                </View>
                
            </View>
          )}
        />
      )}
    </View>
  );
} export {HeaderUserPages}

const style = StyleSheet.create({
    container: {
        padding: 5,
        width: Dimensions.get('window').width, 
        height: 210,
        justifyContent: 'center',
        alignItems: 'center'
    },
    image: {
        marginTop: 20,
        width: 100, 
        height: 100, 
        borderRadius: 50
    },
    following: {
      alignItems: 'center',
      borderRadius: 20,
      flex: 1,
      flexDirection: 'row'
    }
});
