import React, { useState } from 'react';
import { FlatList, StyleSheet, Image, View, Dimensions, Text, TouchableOpacity, AsyncStorage} from 'react-native';
import {SplashScreen} from '../../ScreemFolder/OtherScreen';
import { Feather, AntDesign } from '@expo/vector-icons';

function  HeaderUserPages(props) {
  const [isLoading, setLoading] = React.useState(false);
  const [dataSource, setDataSource ]= React.useState([]);
  const [followed, setFollowed] = React.useState(false);
  const [userData, setUserData] = React.useState([]);
  
  React.useEffect(() => {
    // fetch('http://10.0.2.2:5000/')
    getInfotmation();
    getData();
  }, []);

  const getData = async () => {
    try {
      AsyncStorage.getItem('user').then((value) => {
        const jsonValue = JSON.parse(value);
        setUserData(jsonValue);
        fetch('http://192.168.0.101:19000/get_follow/' +  props.route.params.item.user_id + '/' + jsonValue.user_id)
        .then ((response) => response.json())
        .then ( (res) => { 
          if (res.length == 0) {
            setFollowed(false)
          } else {
            setFollowed(true);
          }
        })
        .catch ((error) => {
          console.log(error);
        })
      })
    } catch(e) {
      console.log(e)
    }
  }

  const getInfotmation=()=>{
    fetch('http://192.168.0.101:19000/author/' + props.route.params.item.user_id)
    .then ((response) => response.json())
    .then ( (res) => { 
      setDataSource(res);
    })
    .catch ((error) => {
      console.log(error);
    })
    .finally(() => {
      setLoading(false) 
    });
  }

  const follow=()=>{
    fetch('http://192.168.0.101:19000/follow/' +  props.route.params.item.user_id + '/' + userData.user_id)
    .then ((response) => response.json())
    .then ( (res) => { 
      setFollowed(true);
      console.log('un')
    })
    .catch ((error) => {
      console.log(error);
    })
  }

  const unFollow=()=> {
    fetch('http://192.168.0.101:19000/unfollow/' +  props.route.params.item.user_id + '/' + userData.user_id)
    .then ((response) => response.json())
    .then ( (res) => { 
      setFollowed(false);
    })
    .catch ((error) => {
      console.log(error);
    })
  }

  return (
    <View>
      {isLoading? <SplashScreen/>: ( 
        <FlatList
          data={dataSource}
          horizontal
          showsHorizontalScrollIndicator={false}
          keyExtractor={item => item.user_id.toString()}
          renderItem={({item}) => (
            <View style={style.container}>
                <Image style={style.image}
                    source={{uri: item.user_avatar}} 
                />
                <Text style={{marginTop: 5, color: 'black', fontSize: 18, fontWeight: 'bold'}}>@{item.username}</Text>
                <View style={{flex:1}}> 
                  <View style={{flex:1, flexDirection: 'row'}}>
                    <Text style={{color: 'black'}}>{item.num_following} following</Text>
                    <Text style={{color: 'black', marginLeft: 10}}>{item.num_follower} follower</Text>
                  </View>
                  {followed? 
                    <TouchableOpacity style={style.following} onPress={() => unFollow()}>
                      <Text style={{color: 'white'}}> 
                        <Feather name="user-minus" size={20} color="white" /> Unfollowing
                      </Text>
                      </TouchableOpacity>: 
                    <TouchableOpacity style={style.following} onPress={() => follow()}>
                      <Text style={{color: 'white'}}> 
                        <AntDesign name="adduser" size={20} color="white" /> Follow
                      </Text>
                    </TouchableOpacity>
                  }
                </View>  
            </View>
          )}
        />
      )}
    </View>
  );
} export {HeaderUserPages}

const style = StyleSheet.create({
    container: {
        padding: 5,
        width: Dimensions.get('window').width, 
        height: 230,
        justifyContent: 'center',
        alignItems: 'center'
    },
    image: {
        marginTop: 20,
        width: 100, 
        height: 100, 
        borderRadius: 50
    },
    following: {
      alignItems: 'center',
      borderRadius: 10,
      justifyContent: 'center',
      backgroundColor: '#aa4fff',
      padding: 8,
      flexDirection: 'row'
    }
});
