import * as React from 'react';
import { View, StyleSheet, Dimensions, Text, FlatList,TouchableOpacity,Image } from 'react-native';
import { TabView, SceneMap } from 'react-native-tab-view';
import {SplashScreen} from '../../ScreemFolder/OtherScreen';

const initialLayout = { width: Dimensions.get('window').width };

function AboutNewsComponent(props) {
  const [index, setIndex] = React.useState(0);
  const [isLoading, setLoading] = React.useState(true);
  const [routes] = React.useState([
    { key: '1', title: 'Story' },
    { key: '2', title: 'User' },
  ]);
  const StoryRoute = () => (
    <View style={{backgroundColor: 'red'}}>

    </View>
  );
  
  const UserRoute = () => (
    <View style={{backgroundColor: 'black', height: 100}}>
    
    </View>
  );
  
  const renderScene = SceneMap({
    1: StoryRoute,
    2: UserRoute,
  });

  return ( 
    <TabView
      style={{backgroundColor: 'white'}}
      navigationState={{ index, routes }}
      renderScene={renderScene}
      onIndexChange={setIndex}
      initialLayout={initialLayout}
    />
  );
} export {AboutNewsComponent};

const styles = StyleSheet.create({
  MainContainer: {
    justifyContent: 'center',
    flex: 1,
    margin: 10,
  },
  story: {
    flexDirection: 'row', 
    padding: 10,
    backgroundColor: 'white',
    width: Dimensions.get('window').width, 
    marginTop: 10,
    height: 140,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  user: {
    margin: 10, 
    flexDirection: 'row', 
    backgroundColor: 'white',
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  imageUser: {
    marginRight: 10,
    width: 70, 
    height: 70, 
    borderRadius: 50
  },
  list: {
    margin: 10, 
    backgroundColor: 'white',
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  }
});
