import React from 'react';
import { StyleSheet, Dimensions, View, TextInput, Text, FlatList } from 'react-native';
import io from "socket.io-client";


function RingScreen() {
  const [dataSource, setDataSource ]= React.useState([]);
  const getData = async () => {
    try {
      const jsonValue = await AsyncStorage.getItem('user');
      console.log('gfgfgfgfg' + jsonValue);
      if (jsonValue != null) {
        const value = JSON.parse(jsonValue);
        console.log('sdsdsđs' + value);
        setDataSource(value);
      } else {
        console.log('null');
      }

    } catch(e) {
      console.log(e)
    }
  }

  React.useEffect(() => {
    getData();
  }, []);
  return (
      <View style={styles.container}>
        <FlatList
            data={dataSource}
            keyExtractor= {item => item.user_id.toString()}
            renderItem={({ item }) => (
              <TouchableOpacity onPress={() => props.navigation.navigate('DetailStory', {item: item})}>         

            <Text>{item.user_id}</Text>
              </TouchableOpacity>
            )}
            showsHorizontalScrollIndicator={false}
          />
    </View>
  )   
} export {RingScreen};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'center',
        marginTop: 25,
    },

});
