import * as React from 'react'; 
import { View , Image, StyleSheet, Text, TouchableWithoutFeedback, Modal, FlatList,TouchableOpacity} from 'react-native';

function ModalChapterContent(props) {
    const [dataSource, setDataSource] = React.useState([]);
    const [modalVisible, setModalVisible] = React.useState(false);
    const data = [
        {id:"1"}
      ]
    return (
        <TouchableWithoutFeedback onPress={() => setModalVisible(!modalVisible)} {...props}>
              <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
              >
                  <View style={style.centeredView}>
            <View style={style.modalView}>
            <FlatList
                data={data}
                renderItem={(item) => (
                <>
                    <View style={{alignItems: "center"}}>
                    <Image style={style.imageModal}
                        source={{uri: props.route.params.item.story_img}} 
                    />
                    <Text style={style.titleModal} numberOfLines={2}>{props.route.params.item.story_title}</Text>
                    </View>
                    <FlatList
                        data={dataSource}
                        keyExtractor= {item => item.chapter_id.toString()}
                        renderItem={({ item }) => (
                            <TouchableOpacity 
                                onPress={() => props.navigation.navigate('Reading', {chapter_id: item.chapter_id, item: item}, 
                                setModalVisible(!modalVisible)
                            )} style={style.chapter}>         
                                <Text numberOfLines={1}>{item.chapter_name}</Text>
                            </TouchableOpacity>
                        )}
                        showsHorizontalScrollIndicator={false}
                    />
                </>
                )}
            />
            <TouchableOpacity
                style={style.closeButtom}
                onPress={() => {
                setModalVisible(!modalVisible);
                }}
            >
                <Text style={style.textStyle}>Hide chapter list</Text>
            </TouchableOpacity>
            </View>
        </View>
                
              </Modal> 
            </TouchableWithoutFeedback>
        
    );
} export {ModalChapterContent}

  const style= StyleSheet.create({
      
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22
  },
  modalView: {
    margin: 10,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 10,
    width: '80%',
    height: '90%',
    //alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5
  },
  
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  },
  imageModal: {
    width: 150,
    height: 200,
    marginTop: 20,
  },
  titleModal: {
    fontSize: 15, 
    fontWeight: 'bold', 
    marginTop: 10, 
    marginBottom: 10
  },
  chapter: {
    padding: 10,
    borderBottomWidth: 0.5,
    borderBottomColor: 'grey'
  },
  closeButtom: {
    backgroundColor: "#aa4fff",
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  }
  });
