import React from 'react'
import { StyleSheet, Text, View, Image , Dimensions} from 'react-native'
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import Slider from '@react-native-community/slider';

const { width, height } = Dimensions.get("window");

const gray = "#91A1BD"


 function HomeScreen () {
    const NeuMorph = ({ children, size, style }) => {
        return (
            <View style={styles.topShadow}>
                <View style={styles.bottomShadow}>
                    <View style={[
                        styles.inner,
                        { width: size || 40, height: size || 40, borderRadius: size / 2 || 40 / 2 },
                        style
                    ]}>
                        {children}
                    </View>
                </View>

            </View>
        )
    }
    return (
        <View style={styles.container}>
            <View style={{ marginHorizontal: 32, marginTop: 32 }}>
                <View style={styles.topContainer}>
                    <NeuMorph>
                        <MaterialIcons name="arrow-back" size={25} color={gray} />
                    </NeuMorph>
                    <View>
                        <Text style={styles.playing}>PLAYING NOW</Text>
                    </View>
                    <NeuMorph>
                        <MaterialIcons name="menu" size={25} color={gray} />

                    </NeuMorph>
                </View>
                <View style= {styles.songArtContainer}>
                    <NeuMorph size= {300}>
                        <Image source= {{uri: "https://firebasestorage.googleapis.com/v0/b/my-music-fe7fc.appspot.com/o/images%2Favatar%2FIMG_20200702_001745.jpg?alt=media&token=9d62c12a-99f4-4b52-a30d-8daf1ec7d940"}}
                        style= {styles.songArt}/>
                    </NeuMorph>
                </View>
                <View style = {styles.songContainer}>
                    <Text style= {styles.title}>Lost it</Text>
                    <Text style= {styles.artist}>Flume ft. Vic Mensa</Text>
                </View>
                <View style = {styles.trackContainer}>
                    <View style= {{flexDirection: "row", justifyContent: "space-between"}}>
                    <Text style= {styles.time}>1:21</Text>
                    <Text style= {styles.time}>3:46</Text>
                    </View>

                </View>
                <View style= {styles.controlsContainer}>
                    <NeuMorph size= {80}>
                    <MaterialIcons name="fast-rewind" size={25} color={gray} />
                    </NeuMorph>
                    <NeuMorph size= {80} style= {{backgroundColor: "#8AAAFF", borderColor: "#8AAAFF"}}>
                    <MaterialIcons name="pause" size={25} color="#FFFFFF" />
                    </NeuMorph>
                    <NeuMorph size= {80}>
                    <MaterialIcons name="fast-forward" size={25} color={gray} />
                    </NeuMorph>
                </View>
            </View>
        </View>
    )
} export {HomeScreen}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#DEE9FD"
    },
    topContainer: {
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "space-between"
    },
    inner: {
        backgroundColor: "#DEE9F7",
        alignItems: "center",
        justifyContent: "center",
        borderColor: "#E2ECFD",
        borderWidth: 1

    },
    topShadow: {
        shadowOffset: {
            width: -6,
            height: -6
        },
        shadowOpacity: 1,
        shadowRadius: 6,
        shadowColor: "#FBFFFF"
    },
    bottomShadow:{
        shadowOffset: {
            width: 6,
            height: 6
        },
        shadowOpacity: 1,
        shadowRadius: 6,
        shadowColor: "#B7C4DD"
    },
    playing: {
        color: gray,
        fontWeight: "800"
    },
    songArtContainer: {
        marginVertical: 32,
        alignItems: "center"
    },
    songArt: {
        width: 300,
        height: 300,
        borderRadius: 150,
        borderColor: "#D7E1F3",
        borderWidth: 10
    },
    songContainer: {

        alignItems: "center"
    },
    title:{
        fontSize: 30,
        color: "#6C7A93",
        fontWeight: "600"
    },
    artist: {
        fontSize: 14,
        marginTop: 6,
        color: gray,
        fontWeight: "500"
    },
    trackContainer: {
        marginTop: 32,
