import React from 'react';
import { StyleSheet, Dimensions, View, TouchableOpacity, Text, Alert, ScrollView } from 'react-native';
import {SplashScreen} from '../users/ScreemFolder/OtherScreen';
import { MaterialCommunityIcons,Ionicons, AntDesign} from '@expo/vector-icons';
import {ReadingHeader} from  '../users/Components/HeaderComponent';

function chapterContent(props) {
  const sendWarning=()=> {
    Alert.alert(
      "Do you want to send warning for this story?",
      "If you choose OK, story's warning times will update. If warning times over three time the account can be deleted.",
      [
        {
          text: "Cancel",
          onPress: () => console.log("Cancel Pressed"),
          style: "cancel"
        },
        { text: "OK", onPress: () => {
          fetch('http://192.168.0.101:19000/warning_email', {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                'gmail': props.route.params.item.user_email,
                'story_id': props.route.params.item.story_id,
                'story_title': props.route.params.item.story_title,
            })
            })
            .then ((response) => response.json())
            .then ( (res) => { 
                console.log('susses');
            })
            .catch ((error) => {
                console.log(error);
          })
        } }
      ],
      { cancelable: false }
    ); 
  }
  const deleteStory=()=> {
    Alert.alert(
      "Do you want to delete this story?",
      "If you choose OK, the story will delete forever on this app.",
      [
        {
          text: "Cancel",
          onPress: () => console.log("Cancel Pressed"),
          style: "cancel"
        },
        { text: "OK", onPress: () => {
          fetch('http://192.168.0.101:19000/delete_story', {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                'gmail': props.route.params.item.user_email,
                'story_id': props.route.params.item.story_id,
                'story_title': props.route.params.item.story_title,
            })
            })
            .then ((response) => response.json())
            .then ( (res) => { 
                console.log('susses');
            })
            .catch ((error) => {
                console.log(error);
          })
        } }
      ],
      { cancelable: false }
    );
  }
  const deleteAccount=()=> {
    Alert.alert(
      "Do you want to delete this account?",
      "If you choose OK, the account will delete forever in this app.",
      [
        {
          text: "Cancel",
          onPress: () => console.log("Cancel Pressed"),
          style: "cancel"
        },
        { text: "OK", onPress: () => {
          fetch('http://192.168.0.101:19000/delete_account', {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                'gmail': props.route.params.item.gmail,
                'user_id': props.route.params.item.author_id,
            })
            })
            .then ((response) => response.json())
            .then ( (res) => { 
                console.log('susses');
            })
            .catch ((error) => {
                console.log(error);
          })
        } }
      ],
      { cancelable: false }
    );
  }
  return (
    <View style={styles.container}>
      <ReadingHeader {...props}/>
        <ScrollView style={{flex:1}}>
            <Text style={{ padding: 20, fontSize: 15, textAlign: 'justify'}}>{props.route.params.item.chapter_content}</Text>
        </ScrollView>
        <View style={styles.tabView}>
            <View style={styles.tabContainer}>
              <TouchableOpacity onPress={()=> sendWarning()}>
                  <AntDesign name="warning" size={24} color="black" />
              </TouchableOpacity>
              <TouchableOpacity onPress={()=> deleteStory()}>
                  <AntDesign name="delete" size={24} color="black" />
              </TouchableOpacity>
              <TouchableOpacity onPress={()=> deleteAccount()}>
                  <MaterialCommunityIcons name="account-remove-outline" size={24} color="black" />
              </TouchableOpacity>
            </View>
        </View>
    </View>
  )   
} export {chapterContent};
 
const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'column',
        alignItems: 'center',
        //marginTop: 25,
        borderColor: '#8a2be2',
    },
    tabView: {
      justifyContent: 'flex-end', 
      backgroundColor: '#aa4fff', 
      width: '100%', 
      height: 40, 
      paddingRight: 5
    },
    tabContainer: {
      flex: 1, 
      height: 40,  
      width: Dimensions.get('window').width,  
      backgroundColor: 'white', 
      alignItems: 'center', 
      justifyContent: 'space-around', 
      flexDirection: 'row'
    },
});
