import * as React from 'react';
import { View, Text, Image,TouchableOpacity, StyleSheet } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';


function ListStoryScreen(props) {
  const [data, setData] = React.useState([]);
  function get_list() {
    fetch('http://192.168.1.6:19000/get_list/1')
    .then ((response) => response.json())
    .then ( (res) => { 
      console.log('list:' + res); 
      setData(res);
    })
    .catch ((error) => {
      console.log(error);
    })
    .finally(() => {
      setLoading(false) 
    });
  }

  React.useEffect(() => {
    get_list();
  }, []);
    return (
      <View>
        <FlatList
          data={data}
          keyExtractor= {item => item.list_id.toString()}
          renderItem={({ item }) =>(
            <View style={styles.container}>
              <TouchableOpacity style={{flexDirection: 'row', margin: 10,}} onPress={() =>props.navigation.navigate('DetailList', {item: item})}>
                <Text style={styles.textName}>{item.list_name}</Text>
              </TouchableOpacity>
            </View>
          )}
        />
      </View>
      
    );
}
export {ListStoryScreen};

var styles = StyleSheet.create({
  container: {
    marginTop: 5,
    marginLeft: 10,
    marginRight:10,
    backgroundColor: 'white',
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  img: {
    width: 80, 
    height: 80, 
    borderRadius: 50
  },
  textName: {
    fontSize: 18, 
    fontWeight: 'bold', 
    marginLeft: 20}
})
